// src/pages/join/NewLicenseForm.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function NewLicenseForm() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "", email: "", phone: "",
    state: "", notes: ""
  });

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    // TODO: send to your backend or email service here
    // fetch("/api/join/new-license", { method: "POST", body: JSON.stringify(form) })
    navigate("/contact-acknowledgement", { state: { type: "New License" } });
  };

  return (
    <section className="section-container">
      <h1 className="section-title">New License – Get Started</h1>
      <p className="lead">Fill out the form and we’ll reach out with next steps.</p>

      <form className="form" onSubmit={onSubmit}>
        <label>Full Name
          <input name="name" value={form.name} onChange={onChange} required />
        </label>
        <label>Email
          <input type="email" name="email" value={form.email} onChange={onChange} required />
        </label>
        <label>Phone
          <input name="phone" value={form.phone} onChange={onChange} />
        </label>
        <label>State/Province
          <input name="state" value={form.state} onChange={onChange} />
        </label>
        <label>Notes (optional)
          <textarea name="notes" rows="4" value={form.notes} onChange={onChange} />
        </label>

        <button type="submit" className="btn-primary">Submit</button>
      </form>
    </section>
  );
}
