import React, { useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./requestPro.css";

export default function RequestForm() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    prefix: "Mr.",
    first: "",
    last: "",
    email: "",
    phone: "",
    requestType: "Referral Form",
    message: "",
    signature: "",
    date: new Date().toISOString().slice(0, 10),
  });

  const [files, setFiles] = useState([]);
  const dropRef = useRef(null);

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleFiles = (fileList) => {
    const arr = Array.from(fileList);
    setFiles(arr);
  };

  const onDrop = (e) => {
    e.preventDefault();
    dropRef.current?.classList.remove("highlight");
    if (e.dataTransfer.files?.length) handleFiles(e.dataTransfer.files);
  };

  const onDragOver = (e) => {
    e.preventDefault();
    dropRef.current?.classList.add("highlight");
  };

  const onDragLeave = () => dropRef.current?.classList.remove("highlight");

  const onSubmit = (e) => {
    e.preventDefault();
    // TODO: send to your backend or email service.
    // Example:
    // await fetch("/api/member-request", {method:"POST", body: formData});
    navigate("/contact-acknowledgement", { state: { type: "Member Request" } });
  };

  return (
    <main className="rf-page">
      <div className="rf-card">
        <header className="rf-header">
          <h1 className="rf-title">Authorization Form</h1>
          <p className="rf-intro">
            Please complete the form to authorize ARAD to process your request. Upload any
            supporting files, provide your contact details, and confirm your consent by signing.
            Your information is kept secure and used only to fulfill your request.
          </p>
        </header>

        <hr className="rf-divider" />

        <form onSubmit={onSubmit} className="rf-form">
          {/* name row */}
          <div className="rf-grid rf-g3">
            <label className="rf-field">
              <span className="rf-label">Prefix</span>
              <select
                className="rf-input rf-select"
                name="prefix"
                value={form.prefix}
                onChange={onChange}
              >
                <option>Mr.</option>
                <option>Ms.</option>
                <option>Mrs.</option>
                <option>Mx.</option>
              </select>
            </label>

            <label className="rf-field">
              <span className="rf-label">First Name</span>
              <input
                className="rf-input"
                name="first"
                value={form.first}
                onChange={onChange}
                placeholder="First Name"
                required
              />
            </label>

            <label className="rf-field">
              <span className="rf-label">Last Name</span>
              <input
                className="rf-input"
                name="last"
                value={form.last}
                onChange={onChange}
                placeholder="Last Name"
                required
              />
            </label>
          </div>

          {/* contact row */}
          <div className="rf-grid rf-g2">
            <label className="rf-field">
              <span className="rf-label">E-mail</span>
              <input
                className="rf-input with-icon email"
                type="email"
                name="email"
                value={form.email}
                onChange={onChange}
                placeholder="you@something.com"
                required
              />
            </label>

            <label className="rf-field">
              <span className="rf-label">Phone</span>
              <input
                className="rf-input with-icon phone"
                name="phone"
                value={form.phone}
                onChange={onChange}
                placeholder="(000) 000-0000"
              />
            </label>
          </div>

          {/* request type */}
          <label className="rf-field">
            <span className="rf-label">Request Type</span>
            <select
              className="rf-input rf-select"
              name="requestType"
              value={form.requestType}
              onChange={onChange}
            >
              <option>Referral Form</option>
              <option>License Status Help</option>
              <option>Nevada Business License Help</option>
              <option>Transfer Out</option>
              <option>General Question</option>
            </select>
          </label>

          {/* message */}
          <label className="rf-field">
            <span className="rf-label">Message (optional)</span>
            <textarea
              className="rf-input rf-textarea"
              rows="4"
              name="message"
              value={form.message}
              onChange={onChange}
              placeholder="Tell us a bit about your request…"
            />
          </label>

          {/* file upload */}
          <div
            ref={dropRef}
            className="rf-upload"
            onDrop={onDrop}
            onDragOver={onDragOver}
            onDragLeave={onDragLeave}
          >
            <input
              id="rf-file"
              type="file"
              multiple
              onChange={(e) => handleFiles(e.target.files)}
              accept="image/*,.pdf"
              style={{ display: "none" }}
            />
            <label htmlFor="rf-file" className="rf-upload-inner">
              <span className="rf-upload-icon" aria-hidden />
              <div className="rf-upload-title">Browse Files</div>
              <div className="rf-upload-sub">Drag and drop files here</div>
              <div className="rf-upload-hint">
                Please upload any related documents. Clear images or PDFs work best.
              </div>
            </label>
          </div>

          {files.length > 0 && (
            <div className="rf-files">
              {files.map((f, i) => (
                <div className="rf-file" key={i} title={f.name}>
                  {f.name}
                </div>
              ))}
            </div>
          )}

          {/* signature + date */}
          <div className="rf-grid rf-g2">
            <label className="rf-field">
              <span className="rf-label">Signature (type your full name)</span>
              <input
                className="rf-input"
                name="signature"
                value={form.signature}
                onChange={onChange}
                placeholder="Type your name"
                required
              />
              <div className="rf-signature">
                <span style={{ opacity: form.signature ? 1 : 0.5 }}>
                  {form.signature || "Sign Here"}
                </span>
              </div>
            </label>

            <label className="rf-field">
              <span className="rf-label">Date</span>
              <input
                className="rf-input"
                type="date"
                name="date"
                value={form.date}
                onChange={onChange}
                required
              />
            </label>
          </div>

          <div className="rf-actions">
            <button className="rf-btn" type="submit">Submit</button>
          </div>
        </form>
      </div>
    </main>
  );
}
