import React, { useState, useEffect, useRef } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import "./Header.css";

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);        // mobile menu
  const [applicantOpen, setApplicantOpen] = useState(false); // dropdown
  const dropdownRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const onClick = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setApplicantOpen(false);
      }
    };
    const onKey = (e) => e.key === "Escape" && setApplicantOpen(false);
    document.addEventListener("click", onClick);
    document.addEventListener("keydown", onKey);
    return () => {
      document.removeEventListener("click", onClick);
      document.removeEventListener("keydown", onKey);
    };
  }, []);

  return (
    <header className="header">
      <div className="logo">
        <NavLink to="/">
          {/* put your logo at public/assets/logo.png */}
          <img src="/assets/logo.png" alt="ARAD Logo" />
        </NavLink>
      </div>

      <button className="menu-toggle" onClick={() => setMenuOpen(v => !v)} aria-label="Menu">
        ☰
      </button>

      <nav className={`nav ${menuOpen ? "open" : ""}`}>
        <NavLink to="/" className="nav-link" onClick={() => setMenuOpen(false)}>
          Current Members
        </NavLink>

        {/* Applicants dropdown */}
        <div
          className={`nav-item dropdown ${applicantOpen ? "open" : ""}`}
          ref={dropdownRef}
          onMouseEnter={() => setApplicantOpen(true)}
          onMouseLeave={() => setApplicantOpen(false)}
        >
          <button className="nav-link dropdown-toggle" onClick={() => setApplicantOpen(v => !v)}>
            Applicants ▾
          </button>

          <div className="dropdown-menu">
            <button
              className="dropdown-item"
              onClick={() => { setApplicantOpen(false); setMenuOpen(false); navigate("/applicants/activate"); }}
            >
              How to Activate Your License
            </button>
            <button
              className="dropdown-item"
              onClick={() => { setApplicantOpen(false); setMenuOpen(false); navigate("/applicants/transfer"); }}
            >
              How to Transfer Your License
            </button>
            <button
              className="dropdown-item"
              onClick={() => { setApplicantOpen(false); setMenuOpen(false); navigate("/applicants/non-mls"); }}
            >
              Practicing Real Estate Without the MLS
            </button>
          </div>
        </div>

        <NavLink to="/partnership" className="nav-link" onClick={() => setMenuOpen(false)}>
          Non-MLS Real Estate
        </NavLink>

        <NavLink to="/join" className="nav-link cta" onClick={() => setMenuOpen(false)}>
          Join Now
        </NavLink>
      </nav>
    </header>
  );
}
