import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./Header.css";

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <header className="header">
      <div className="logo">
        <NavLink to="/">
          <img src="assets/logo.png" alt="ARAD Logo" />
        </NavLink>
      </div>

      {/* Hamburger Button */}
      <button className="menu-toggle" onClick={toggleMenu}>
        ☰
      </button>

      <nav className={`nav ${menuOpen ? "open" : ""}`}>
        <NavLink to="/" className={({ isActive }) => isActive ? "nav-link active" : "nav-link"} onClick={() => setMenuOpen(false)}>
          Curent Member
        </NavLink>
        {/* Applicant dropdown */}
        <div
          className={`nav-item dropdown ${applicantOpen ? "open" : ""}`}
          ref={dropdownRef}
          onMouseEnter={() => setApplicantOpen(true)}   // hover (desktop)
          onMouseLeave={() => setApplicantOpen(false)}
        >
          <button
            className="nav-link dropdown-toggle"
            onClick={() => setApplicantOpen((v) => !v)} // click (mobile/desktop)
            aria-haspopup="true"
            aria-expanded={applicantOpen}
          >
            Applicants ▾
          </button>

          <div className="dropdown-menu">
            <button
              className="dropdown-item"
              onClick={() => {
                setApplicantOpen(false);
                setMenuOpen(false);
                navigate("/applicants/activate");
              }}
            >
              How to Activate Your License
            </button>

            <button
              className="dropdown-item"
              onClick={() => {
                setApplicantOpen(false);
                setMenuOpen(false);
                navigate("/applicants/transfer");
              }}
            >
              How to Transfer Your License
            </button>

            <button
              className="dropdown-item"
              onClick={() => {
                setApplicantOpen(false);
                setMenuOpen(false);
                navigate("/applicants/non-mls");
              }}
            >
              Practicing Real Estate Without the MLS
            </button>
          </div>
        </div>
        <NavLink to="/partnership" className={({ isActive }) => isActive ? "nav-link active" : "nav-link"} onClick={() => setMenuOpen(false)}>
          None MLS
        </NavLink>
        <NavLink to="/contact" className={({ isActive }) => isActive ? "nav-link active" : "nav-link"} onClick={() => setMenuOpen(false)}>
          join Now
        </NavLink>
      </nav>
    </header>
  );
}
