// src/pages/join/TransferForm.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function TransferForm() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "", email: "", phone: "",
    currentBroker: "", licenseNumber: "", notes: ""
  });

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    // TODO: send to your backend or email service here
    // fetch("/api/join/transfer", { method: "POST", body: JSON.stringify(form) })
    navigate("/contact-acknowledgement", { state: { type: "Transfer License" } });
  };

  return (
    <section className="section-container">
      <h1 className="section-title">Transfer License – Start Here</h1>
      <p className="lead">Tell us a bit about your current status and we’ll handle the transfer.</p>

      <form className="form" onSubmit={onSubmit}>
        <label>Full Name
          <input name="name" value={form.name} onChange={onChange} required />
        </label>
        <label>Email
          <input type="email" name="email" value={form.email} onChange={onChange} required />
        </label>
        <label>Phone
          <input name="phone" value={form.phone} onChange={onChange} />
        </label>
        <label>Current Brokerage
          <input name="currentBroker" value={form.currentBroker} onChange={onChange} />
        </label>
        <label>License Number
          <input name="licenseNumber" value={form.licenseNumber} onChange={onChange} />
        </label>
        <label>Notes (optional)
          <textarea name="notes" rows="4" value={form.notes} onChange={onChange} />
        </label>

        <button type="submit" className="btn-primary">Submit</button>
      </form>
    </section>
  );
}
