import React from "react";
import "./Pages.css";

export default function Partnership() {
  const baseUrl = process.env.REACT_APP_API_BASE_URL;

  const showModal = () => {
    const modal = document.getElementById("thankYouModal");
    if (modal) modal.style.display = "flex";
  };

  const closeModal = () => {
    const modal = document.getElementById("thankYouModal");
    if (modal) modal.style.display = "none";
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  const formData = {
    fullName: e.target.fullName.value.trim(),
    email: e.target.email.value.trim(),
    phone: e.target.phone.value.trim(),
    organization: e.target.organization.value.trim(),
    industry: e.target.industry.value,
    address: e.target.address.value.trim(),
    partnershipType: e.target.partnershipType.value,
    message: e.target.message.value.trim(),
  };
    // Client-side validation for required fields
  let errors = [];
  if (!formData.fullName) errors.push("Full Name is required.");
  if (!formData.email) errors.push("Email is required.");
  else {
    // Basic email format check
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      errors.push("Please enter a valid email address.");
    }
  }

  if (errors.length > 0) {
    alert(errors.join("\n"));
    return;
  }

    try {
      const response = await fetch(`${baseUrl}/api/partnership`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        showModal();
        e.target.reset();
      } else {
        alert("Submission failed. Please try again.");
      }
    } catch (error) {
      console.error("Submission error:", error);
      alert("An error occurred. Please try again.");
    }
  };

  return (
    <section className="partnership-section">
      <div className="form-container">
        <h1>Partner with Arad Company</h1>
        <p>
          We're always open to collaborating with new partner.
        </p>

        <form onSubmit={handleSubmit} className="partnership-form">
          <div className="form-group">
            <label>Full Name*</label>
            <input type="text" name="fullName" required />
          </div>

          <div className="form-group">
            <label>Email*</label>
            <input type="email" name="email" required />
          </div>

          <div className="form-group">
            <label>Phone</label>
            <input type="text" name="phone" />
          </div>

          <div className="form-group">
            <label>Organization Name</label>
            <input type="text" name="organization" />
          </div>

          <div className="form-group">
            <label>State</label>
            <select name="industry">
              <option>Nevada</option>
              <option>California</option>

            </select>
          </div>

          <div className="form-group">
            <label>Location</label>
            <input type="text" name="address" />
          </div>

          <div className="form-group">
            <label>Partnership Type</label>
            <select name="partnershipType">
              <option>Current Member</option>
              <option>Non MLS</option>
              <option>New License</option>
              <option>transferring License</option>
              <option>Other</option>
            </select>
          </div>

          <div className="form-group">
            <label>Message</label>
            <textarea name="message" rows="4" />
          </div>

          <button type="submit">Submit Request</button>
        </form>
      </div>

      {/* Custom Modal */}
      <div
        className="modal-overlay"
        id="thankYouModal"
        style={{ display: "none" }}
        onClick={(e) => {
          if (e.target.id === "thankYouModal") closeModal();
        }}
      >
        <div className="modal-box">
          <button className="modal-close" onClick={closeModal}>
            &times;
          </button>
          <h2>Thank You!</h2>
          <div className="modal-content">
            <p>
              We appreciate your interest in partnering with AKI Battery
              Recycling. We'll reach out to you shortly.
            </p>
          </div>
        </div>
      </div>
    </section>
  );
}
